let MachineFilter = iff("{Machine}" == "*" or isempty("{Machine}"), "", "{Machine}");

// Installed apps
let InstalledApps =
    uberAgent_Application_ApplicationInventory_CL
    | where isempty(MachineFilter) or Computer has MachineFilter
    | project Computer, AppName = tostring(DisplayName_s), Publisher = tostring(Publisher_s)
    | summarize by Computer, AppName, Publisher;

// Launched apps (from process data)
let LaunchedApps =
    uberAgent_Process_ProcessDetail_CL
    | where isempty(MachineFilter) or Computer has MachineFilter
    | summarize LaunchCount = count() by Computer, AppName = tostring(ProcName_s);

// Installed apps with launch info (leftouter join)
let InstalledJoin =
    InstalledApps
    | join kind=leftouter (LaunchedApps) on Computer, AppName
    | extend WasLaunched = iff(isnull(LaunchCount) or LaunchCount == 0, "No", "Yes")
    | project Computer, AppName, Publisher, LaunchCount = coalesce(LaunchCount, 0), WasLaunched, Source = "Installed";

// Launched-only apps that were not in inventory (leftanti join)
let LaunchedOnly =
    LaunchedApps
    | join kind=leftanti (InstalledApps) on Computer, AppName
    | extend Publisher = "Not Installed", WasLaunched = "Yes"
    | project Computer, AppName, Publisher, LaunchCount, WasLaunched, Source = "Launched Only";

// Combine both
InstalledJoin
| union LaunchedOnly
| sort by WasLaunched asc, LaunchCount desc, AppName asc
