let MachineFilter = iff("{Machine}" == "*" or isempty("{Machine}"), "", "{Machine}");
let UserFilter = iff("{User}" == "*" or isempty("{User}"), "", "{User}");
let CatalogFilter = iff("{Catalog}" == "*" or isempty("{Catalog}"), "", "{Catalog}");
let SourceIPFilter = iff("{SourceIP}" == "*" or isempty("{SourceIP}"), "", "{SourceIP}");
let TargetIPFilter = iff("{TargetIP}" == "*" or isempty("{TargetIP}"), "", "{TargetIP}");

let AppNames = 
    uberAgent_Application_AppNameIdMapping_CL
    | summarize AppName = any(AppName_s) by AppId_s;

uberAgent_Process_ProcessDetail_CL
| where SessionID_d > 0
| where isnotempty(AppId_s) and AppId_s !contains "OS"
| where isempty(MachineFilter) or Computer has MachineFilter
| where isempty(UserFilter) or ProcUser_s has UserFilter
| where isempty(SourceIPFilter) or Source_s has SourceIPFilter
| where isempty(TargetIPFilter) or SourceType_s has TargetIPFilter
| join kind=leftouter (AppNames) on AppId_s
| summarize 
    AppNameCount = dcount(AppName),
    VersionCount = dcount(AppVersion_s),
    UserCount = dcount(ProcUser_s),
    HostCount = dcount(Computer)

