    uberAgent_Application_AppNameIdMapping_CL
    | summarize AppName = any(AppName_s) by AppId_s
) on $left.SessionFgAppId_s == $right.AppId_s
| extend ["App in foreground (%)"] = round(CountSessionFgAppId * 100.0 / TotalCount, 1)
| project AppName, ["App in foreground (%)"]
| order by ["App in foreground (%)"] desc
