let MachineFilter = iff("{Machine}" == "*" or isempty("{Machine}"), "", "{Machine}");
let UserFilter = iff("{User}" == "*" or isempty("{User}"), "", "{User}");

let dns = uberAgentESA_Process_DnsQuery_CL
| where DnsRiskHighEntropy_d == 1
| where isempty(MachineFilter) or Computer has MachineFilter
| summarize Count = count(), LastSeen = max(TimeGenerated) by ProcGUID_g, DnsRequest_s, ProcName_s, Computer;

let proc = uberAgentESA_Process_ProcessStop_CL
| project ProcGUID_g, ProcUser_s;

dns
| join kind=leftouter proc on ProcGUID_g
| where isempty(UserFilter) or ProcUser_s has UserFilter
| project LastSeen, DnsRequest_s, ProcName_s, ProcUser_s, Computer, Count
| top 20 by Count desc

let MachineFilter = iff("{Machine}" == "*" or isempty("{Machine}"), "", "{Machine}");
let UserFilter = iff("{User}" == "*" or isempty("{User}"), "", "{User}");

let dns = uberAgentESA_Process_DnsQuery_CL
| where isempty(MachineFilter) or Computer has MachineFilter
| summarize RequestCount = count() by bin(TimeGenerated, 5m), Computer, ProcName_s, ProcGUID_g
| where RequestCount > 10;

let proc = uberAgentESA_Process_ProcessStop_CL
| project ProcGUID_g, ProcUser_s;

dns
| join kind=leftouter proc on ProcGUID_g
| where isempty(UserFilter) or ProcUser_s has UserFilter
| project TimeGenerated, Computer, ProcName_s, ProcUser_s, RequestCount
| order by Computer, ProcName_s, TimeGenerated asc

let MachineFilter = iff("{Machine}" == "*" or isempty("{Machine}"), "", "{Machine}");
let UserFilter = iff("{User}" == "*" or isempty("{User}"), "", "{User}");

let dns = uberAgentESA_Process_DnsQuery_CL
| where isempty(MachineFilter) or Computer has MachineFilter
| extend TLD = tostring(split(DnsRequest_s, ".")[-1])
| summarize Count = count() by ProcGUID_g, Computer, TLD;

let proc = uberAgentESA_Process_ProcessStop_CL
| project ProcGUID_g, ProcUser_s;

dns
| join kind=leftouter proc on ProcGUID_g
| where isempty(UserFilter) or ProcUser_s has UserFilter
| summarize Count = sum(Count) by TLD
| order by Count asc

let knownGood = dynamic(["chrome.exe", "firefox.exe", "msedge.exe", "iexplore.exe", "system"]);
let MachineFilter = iff("{Machine}" == "*" or isempty("{Machine}"), "", "{Machine}");
let UserFilter = iff("{User}" == "*" or isempty("{User}"), "", "{User}");

let dns = uberAgentESA_Process_DnsQuery_CL
| where isempty(MachineFilter) or Computer has MachineFilter
| where ProcName_s !in (knownGood)
| summarize Count = count() by ProcGUID_g, ProcName_s;

let proc = uberAgentESA_Process_ProcessStop_CL
| project ProcGUID_g, ProcUser_s;

dns
| join kind=leftouter proc on ProcGUID_g
| where isempty(UserFilter) or ProcUser_s has UserFilter
| summarize TotalCount = sum(Count) by ProcName_s
| top 20 by TotalCount desc

