let MachineFilter = iff("{Machine}" == "*" or isempty("{Machine}"), "", "{Machine}");
let UserFilter = iff("{User}" == "*" or isempty("{User}"), "", "{User}");

let dns = uberAgentESA_Process_DnsQuery_CL
| summarize DNSRequests = count(), UniqueDomains = dcount(DnsRequest_s), EntropyHits = countif(DnsRiskHighEntropy_d == 1)
  by ProcGUID_g, Computer, ProcName_s;

let proc = uberAgentESA_Process_ProcessStop_CL
| project ProcGUID_g, ProcUser_s, ProcCmdline_s, ProcLifetimeMs_d;

dns
| join kind=leftouter proc on ProcGUID_g
| where (isempty(MachineFilter) or Computer has MachineFilter)
  and (isempty(UserFilter) or ProcUser_s has UserFilter)
| project Computer, ProcName_s, ProcCmdline_s, ProcUser_s, DNSRequests, UniqueDomains, EntropyHits, ProcLifetimeMs_d
| order by EntropyHits desc

let MachineFilter = iff("{Machine}" == "*" or isempty("{Machine}"), "", "{Machine}");
let UserFilter = iff("{User}" == "*" or isempty("{User}"), "", "{User}");

let dns = uberAgentESA_Process_DnsQuery_CL
| where isempty(MachineFilter) or Computer has MachineFilter
| summarize RequestCount = count(), LastSeen = max(TimeGenerated) by ProcGUID_g, Computer;

let proc = uberAgentESA_Process_ProcessStop_CL
| project ProcGUID_g, ProcUser_s;

let dns_with_user = dns
| join kind=leftouter proc on ProcGUID_g
| where isempty(UserFilter) or ProcUser_s has UserFilter;

let netinfo = uberAgent_System_NetworkConfigInformation_CL
| summarize arg_max(TimeGenerated, *) by Computer;

dns_with_user
| join kind=leftouter netinfo on Computer
| project LastSeen, Computer, ProcUser_s, NetworkConfigIPv4_s, NetworkConfigMACAddress_s, NetworkConfigIsConnectedToInternet_b, RequestCount
| order by LastSeen desc
