let MachineFilter = iff('{Machine}' == '*' or isempty('{Machine}'), '', '{Machine}');
let UserFilter = iff('{User}' == '*' or isempty('{User}'), '', '{User}');

// Pull logon-started processes with startup and runtime timing
let LogonProcesses = 
    uberAgent_Process_LogonProcesses_CL
    | where isempty(MachineFilter) or Computer has MachineFilter
    | where isempty(UserFilter) or ProcUser_s has UserFilter
    | extend StartupSeconds = round(ProcStartTimeRelativeMs_d / 1000.0, 2), RuntimeSeconds = round(ProcLifetimeMs_d / 1000.0, 2)
    | project Computer, ProcUser_s, ProcName_s, StartupSeconds, RuntimeSeconds;

// Pull input delay statistics
let ProcessStats = 
    uberAgent_Process_ProcessStatistics_CL
    | where isempty(MachineFilter) or Computer has MachineFilter
    | where isempty(UserFilter) or ProcUser_s has UserFilter
    | summarize MaxInputDelayMs = max(ProcInputDelayMaxMs_d) by Computer, ProcName_s;

// Now join them together
LogonProcesses
| join kind=leftouter (ProcessStats) on Computer, ProcName_s
| project Computer, ProcUser_s, ProcName_s, StartupSeconds, RuntimeSeconds, MaxInputDelayMs
| order by StartupSeconds asc
